namespace Magestore\Affiliateplusprogram\Block\Adminhtml\Program;

/**
 * Class Edit
 * @package Magestore\Affiliateplusprogram\Block\Adminhtml\Program
 */
class Edit extends \Magento\Backend\Block\Widget\Form\Container
{
    /**
     * @param \Magento\Backend\Block\Widget\Context $context
     * @param \Magento\Framework\Registry $registry
     * @param array $data
     */
    public function __construct(
        \Magento\Backend\Block\Widget\Context $context,
        \Magento\Framework\Registry $registry,
        array $data = []
    ) {
        $this->_coreRegistry = $registry;
        parent::__construct($context, $data);
    }
    /**
     * {@inheritdoc}
     */
    protected function _construct()
    {
        $this->_objectId = 'program_id';
        $this->_blockGroup = 'Magestore_Affiliateplusprogram';
        $this->_controller = 'adminhtml_program';

        parent::_construct();

        $this->buttonList->update('save', 'label', __('Save Program'));
        $this->buttonList->update('delete', 'label', __('Delete Program'));

        $this->buttonList->add(
            'saveandcontinue',
            [
                'label' => __('Save and Continue Edit'),
                'class' => 'save',
                'data_attribute' => [
                    'mage-init' => ['button' => ['event' => 'saveAndContinueEdit', 'target' => '#edit_form']],
                ],
            ],
            -100
        );

        $this->_formScripts[] = "
            function toggleEditor() {
                if (tinyMCE.getInstanceById('block_content') == null) {
                    tinyMCE.execCommand('mceAddControl', false, 'block_content');
                } else {
                    tinyMCE.execCommand('mceRemoveControl', false, 'block_content');
                }
            }

        ";
    }
}
